mapper.select:
select

from dict_item

	user_id = #{userId,jdbcType=BIGINT}

		and tenant_id = #{tenantId,jdbcType=BIGINT}


	and (dict_item_code =
	#{dictItemCode,jdbcType=VARCHAR} or dict_item_name
	=
	#{dictItemName,jdbcType=VARCHAR})

		and id != #{id,jdbcType=BIGINT}

		select

		from dict_item


        	id = #{id,jdbcType=BIGINT}


        	and tenant_id = #{tenantId,jdbcType=BIGINT}


	        and user_id = #{userId,jdbcType=BIGINT}


	        and status = #{status,jdbcType=CHAR}


	        and color = #{color,jdbcType=VARCHAR}


	        and dict_entry_code = #{dictEntryCode,jdbcType=VARCHAR}


	        and dict_item_code = #{dictItemCode,jdbcType=VARCHAR}


	        and dict_item_name = #{dictItemName,jdbcType=VARCHAR}


	        and dict_item_order = #{dictItemOrder,jdbcType=BIGINT}


	        and remark = #{remark,jdbcType=VARCHAR}
