mapper.select:
select

from biz_pmml_info


		ID = #{id,jdbcType=VARCHAR}


		and model_name = #{modelName,jdbcType=VARCHAR}


		and project_id = #{projectId,jdbcType=BIGINT}


		and project_name = #{projectName,jdbcType=VARCHAR}


		and pmml_file_path = #{pmmlFilePath,jdbcType=VARCHAR}


		and user_id = #{userId,jdbcType=BIGINT}


		and task_id = #{taskId,jdbcType=VARCHAR}


		and inst_id = #{instId,jdbcType=VARCHAR}


		and flow_id = #{flowId,jdbcType=VARCHAR}


		and act_id = #{actId,jdbcType=VARCHAR}


		and algorithm = #{algorithm,jdbcType=VARCHAR}


		and create_time = #{createTime,jdbcType=BIGINT}


		and status = #{status,jdbcType=VARCHAR}


ORDER BY
create_time desc
