mapper.select:
select

from biz_project_train
where 1=1

	and train_type = #{trainType,jdbcType=VARCHAR}


	and train_name like
	concat(concat('%',#{trainName,jdbcType=VARCHAR}),'%')


	and train_code = #{trainCode,jdbcType=VARCHAR}


	and creator_id = #{creatorId,jdbcType=BIGINT}


	and is_product = #{isProduct,jdbcType=VARCHAR}



		ORDER BY PRODUCT_DATE desc


		ORDER BY CREATE_DATE desc
